function syntacticallyNeeded()

a = 14/3;
b = 1.0;
[p,q] = rat((a-b)/b);
tmax = q*2*pi;

t = linspace(0,tmax,300);
x = (a-b)*cos(t) + b*cos((a-b)/b*t);
y = (a-b)*sin(t) - b*sin((a-b)/b*t);

title('zykloide mit b=1');
%legend('a=14/3');
plot(x,y,'g');

a = 3;
[p,q] = rat((a-b)/b);
tmax = q*2*pi;

t = linspace(0,tmax,300);
x = (a-b)*cos(t) + b*cos((a-b)/b*t);
y = (a-b)*sin(t) - b*sin((a-b)/b*t);

%legend('a=3');
plot(x,y,'c');

end %function syntacticallyNeeded()
